$(document).ready(function(){$("div.demo-show h3").add("div.demo-show2 h3").hover(function(){$(this).addClass("hover")},function(){$(this).removeClass("hover")})});$(document).ready(function(){$("div.demo-show:eq(0) > div").hide();$("div.demo-show:eq(0) > h3").click(function(){$(this).next().slideToggle("fast")})});$(document).ready(function(){$("div.demo-show:eq(1) > div:gt(0)").hide();$("div.demo-show:eq(1) > h3").click(function(){$(this).next("div:hidden").slideDown("fast").siblings("div:visible").slideUp("fast")})});$(document).ready(function(){$("div.demo-show2:eq(0) > div").hide();$("div.demo-show2:eq(0) > h3").click(function(){$(this).next("div").slideToggle("fast").siblings("div:visible").slideUp("fast")})});$(document).ready(function(){$("div.demo-show2:eq(1) > div").hide();$("div.demo-show2:eq(1) > h3").click(function(){var b=$(this).next();var a=b.siblings("div:visible");if(a.length){a.slideUp("fast",function(){b.slideToggle("fast")})}else{b.slideToggle("fast")}})});